#include<stdio.h>void reverse(int* p, int n){    int* q, t;    for (q = p + n - 1; p < q; p++, q--)    {        t = *q;        *q = *p; // 修正这里，先更新 *q        *p = t; // 然后更新 *p    }}int main(){    int a[10] = { 0 };    printf("输入十个数据:");    for (int i = 0; i < 10; i++)        scanf("%d", a[i]); // 修正这里，使用 &a[i]    reverse(a, 10);    for (int i = 0; i < 10; i++)        printf("%3d", a[i]);    return 0;}